def start(update, context):
    chat_id = update.effective_chat.id
    update.message.reply_text(word)
    update.message.reply_text ("Tap and hold the microphone icon and pronounce the word")
    chat_id = update.effective_chat.id
    update.message.reply_text("Or enter /info to see phonetic transcription and definitions" "\n"
                              "/examples to see the word in contexts" "\n"
                              "/correct to hear the correct pronounciation" "\n"
                              "to try another word enter /restart")

def info(update, context):
    ipa_trans = ipa.convert(word)
    chat_id = update.effective_chat.id
    update.message.reply_text(ipa_trans)
    synsets = wn.synsets(word)
    for synset in synsets:
        update.message.reply_text(synset.definition())


def examples(update, context):
    chat_id = update.effective_chat.id
    synsets = wn.synsets(word)
    for synset in synsets:
        update.message.reply_text(synset.examples())


def correct(update, context):
    chat_id = update.effective_chat.id
    update.message.reply_text("Correct pronounciation:")
    update.message.reply_audio(audio = open('word.mp3', 'rb'))
 


def restart(update, context):
    chat_id = update.effective_chat.id
    global word
    word = random.choice(words_dictionary)
    update.message.reply_text(word)

    global tts
    global audio
    tts = gtts.gTTS(word, lang = 'en')
    audio = tts.save('word.mp3')
  
    
   

def main():
    updater = Updater(token)
    updater.dispatcher.add_handler(CommandHandler("start", start))
    updater.dispatcher.add_handler(CommandHandler("info", info))
    updater.dispatcher.add_handler(CommandHandler("examples", examples))
    updater.dispatcher.add_handler(CommandHandler("correct", correct))
    updater.dispatcher.add_handler(CommandHandler("restart", restart))
    updater.start_polling()
    updater.idle()



if __name__ == "__main__":
     main()
     




    




  
